header.header {
    background: #111011;
    display: flex;
    align-items: center;
    height: 87px;
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    z-index: 13;
    transition: 0.5s;
}

.header__logo {
    img {
        max-width: 50px;
    }
}

.header__nav {
    ul {
        text-align: center;

        li {
            display: inline-block;
            vertical-align: middle;
            margin-right: 24px;

            &:last-child {
                margin-right: 0;
            }

            a {
                color: #fff;
                font-size: 14px;
                font-weight: 300;
                font-family: "Unbounded", sans-serif;
                transition: 0.3s;
                position: relative;

                &::after {
                    content: '';
                    display: block;
                    margin: 0 auto;
                    height: 1px;
                    position: absolute;
                    left: 0;
                    right: 0;
                    width: 0;
                    background: rgba(#84F7A0, 0.4);
                    transition: 0.3s;
                    bottom: -8px;
                }

                &:hover {
                    &::after {
                        width: 100%;
                    }
                }
            }
        }
    }
}

.menubtn,
button.close {
    display: none;
}



header nav {
    display: flex;
    align-items: center;
    justify-content: center;
}

.header__language {
    width: 52px;
    margin-left: 28px;
    position: relative;

    span {
        display: flex;
        align-items: center;
        text-transform: uppercase;
        font-family: "Unbounded", sans-serif;
        font-size: 14px;
        font-weight: 300;
        line-height: 140%;
        color: #fff;
        padding-left: 6px;
        height: 28px;
        cursor: pointer;
        transition: 0.3s;

        &::after {
            content: '';
            display: inline-block;
            width: 16px;
            height: 16px;
            background: url('../img/chevron-down.svg') no-repeat center / 100%;
            margin-left: 4px;
            position: absolute;
            right: 0;
            transition: 0.3s;
            margin-top: -2px;
        }

        &:hover {
            background: #212021;
        }
    }

    &.active {
        span {
            background: #212021;

            &::after {
                transform: rotate(-180deg);
            }
        }

        ul {
            opacity: 1;
            z-index: 1;
            visibility: visible;
            transform: translateY(0);
        }
    }

    ul {
        position: absolute;
        top: 28px;
        left: 0;
        background: #212021;
        width: 100%;
        text-align: left;
        opacity: 0;
        z-index: -1;
        visibility: hidden;
        transform: translateY(10px);
        transition: 0.3s;


        li {
            width: 100%;
            a {
                font-family: "Unbounded", sans-serif;
                font-size: 14px;
                font-weight: 300;
                line-height: 140%;
                padding: 2px 2px 2px 6px;
                text-transform: uppercase;
                display: block;

                &:hover {
                    background: rgba(#84F7A0, 0.1);
                }

                &::after {
                    display: none !important;
                }
            }
        }
    }
}

@media(max-width:991.8px) {
    .header__nav {
        // display: none;
        position: fixed;
        right: 0;
        top: 0;
        width: 400px;
        height: 100vh;
        background: #000;
        z-index: 15;
        transition: 0.5s;
        overflow: auto;
        box-shadow: 0 0 10px var(--green);
        transform: translateX(120%);

        &.show {
            transform: translateX(0);
        }

        nav {
            padding: 100px 40px 40px 40px;
        }

        ul {
            text-align: left;

            li {
                display: block;
                margin-bottom: 20px;

                a {
                    font-size: 18px;
                }
            }
        }
    }

    header.header .col-md-8 {
        text-align: right;
    }

    .menubtn {
        display: inline-block;
        background: transparent;
        border: none;
        outline: none;
        padding: 0;
        margin-left: 40px;
    }

    button.close {
        display: inline-block;
        position: absolute;
        right: 30px;
        top: 30px;
        border: none;
        background: transparent;
        outline: none;
        padding: 0;
        width: 40px;
        height: 40px;
        z-index: 15;

        img {
            width: 24px;
        }
    }

    .header__nav nav {
        display: block;
    }

    .header__language {
        margin-left: 0;
        margin-top: 40px;
        width: 64px;

        span {
            font-size: 18px;

            &::after {
                width: 20px;
                height: 20px;
            }
        }

        ul {
            li {
                margin-bottom: 0;
            }
        }
    }


}

@media(max-width:575.8px) {
    .menubtn {
        margin-left: 30px;
    }

    .header__nav.show {
        width: 100%;
    }

    .header__nav {
        nav {
            z-index: 10;
            position: relative;
        }

        &::after {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            display: block;
            margin: 0 auto;
            opacity: 0.03;
            position: absolute;
            width: 100%;
            height: 300px;
            background: url('../img/logbig.png') no-repeat center / contain;
        }
    }


}

