.modalwrapper {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100vh;
    background: rgba(17, 16, 17, 1);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    z-index: -1;
    visibility: hidden;
    transition: 0.3s;

    &.active {
        opacity: 1;
        z-index: 16;
        visibility: visible;
    }
}

.tybox {
    width: 385px;
    padding: 24px 24px 30px 24px;
    border: 1px solid #84F7A0;
    position: relative;




    &::after {
        content: "";
        display: inline-block;
        width: 30px;
        height: 30px;
        border-left: 1px solid #84F7A0;
        background: #111011;
        position: absolute;
        right: -15px;
        top: -15px;
        z-index: 5;
        overflow: hidden;
        -webkit-transform: rotate(-45deg);
        transform: rotate(-45deg);
    }

    p {
        margin-bottom: 22px;
        color: rgb(245, 245, 245);
        font-size: 14px;
        font-family: "ruberoid";
        line-height: 135%;
    }

    .title {
        color: #42795C;
        margin-bottom: 16px;
        font-weight: 500;
        padding-right: 30px;
        font-size: 14px;
        font-family: "Unbounded", sans-serif;
        line-height: 140%;
    }

    .greenbtn {
        max-width: 100%;
        width: 100%;
        text-align: center;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        min-height: 52px;
    }

    .closety {
        position: absolute;
        outline: none;
        border: none;
        background: transparent;
        padding: 0;
        top: 21px;
        right: 24px;
    }
}

