.herosection {
    min-height: calc(100vh - 87px);
    display: flex;
    align-items: center;
    position: relative;
    overflow: hidden;
    background: #000;
}

.herosection__info {
    position: relative;
    z-index: 2;

    h1 {
        color: #F5F5F5;
        font-family: "univermag";
        font-size: 58px;
        font-weight: 400;
        line-height: 90%;
        margin-bottom: 40px;

        span {
            color: var(--green);
        }
    }

    p {
        color: var(--light-grey);
        margin-bottom: 46px;
        font-size: 14px;
        max-width: 486px;
        line-height: 135%;
    }

    .greenbtn {
        min-width: 174px;
        text-align: center;
    }
}

.herosection__video {

    video {
        width: 220%;
        transform: translateX(-26%);
    }
}

.about {
    padding: 105px 0 110px;
    position: relative;
    overflow: hidden;

    img.aboutbg {
        mix-blend-mode: soft-light;
        position: absolute;
        left: 0;
        top: 0;
    }

    &::after,
    &::before {
        content: '';
        position: absolute;
        left: 0;
        display: block;
        height: 8px;
        width: 100%;
        background: url('../img/lines.png') repeat-x;
    }

    &::after {
        top: 0;
    }

    &::before {
        bottom: 0;
    }

    h2 {
        margin-bottom: 62px;
    }

    .row {
        margin-left: -18px;
        margin-right: -18px;

        .col-xl-4 {
            padding-left: 18px;
            padding-right: 18px;
        }
    }
}

.aboutbox {
    position: relative;
    background: rgba(#84F7A0, 0.02);
    padding: 16px 24px;
    min-height: 145px;
    display: block;
    cursor: pointer;

    p {
        margin: 0;
        font-size: 14px;
        color: var(--light-grey);
    }

    &::after {
        content: '';
        position: absolute;
        display: inline-block;
        width: 6px;
        height: 6px;
        border-top: 1px solid rgb(132, 247, 160);
        border-left: 1px solid rgb(132, 247, 160);
        left: -5px;
        top: -5px;
        transition: 0.3s;
        animation-timing-function: ease-in-out;
    }

    &::before {
        content: '';
        position: absolute;
        display: inline-block;
        width: 6px;
        height: 6px;
        border-bottom: 1px solid rgb(132, 247, 160);
        border-left: 1px solid rgb(132, 247, 160);
        bottom: -5px;
        left: -5px;
        transition: 0.3s;
        animation-timing-function: ease-in-out;
    }

    p {
        &::after {
            content: '';
            position: absolute;
            display: inline-block;
            width: 6px;
            height: 6px;
            border-top: 1px solid rgb(132, 247, 160);
            border-right: 1px solid rgb(132, 247, 160);
            right: -5px;
            top: -5px;
            transition: 0.3s;
        }

        &::before {
            content: '';
            position: absolute;
            display: inline-block;
            width: 6px;
            height: 6px;
            border-bottom: 1px solid rgb(132, 247, 160);
            border-right: 1px solid rgb(132, 247, 160);
            bottom: -5px;
            right: -5px;
            transition: 0.3s;
        }
    }

    &:hover {
        background: rgba(#84F7A0, 0.04);

        &::after {
            left: 0;
            top: 0;
        }

        &::before {
            bottom: 0;
            left: 0;
        }

        p {
            &::after {
                right: 0;
                top: 0;
            }

            &::before {
                bottom: 0;
                right: 0;
            }
        }
    }
}

.about__videosect {
    margin-top: 125px;
    position: relative;
    z-index: 2;

    h3, h2 {
        color: #fff;
        line-height: 100%;
        margin-bottom: 50px;
        text-align: center;
    }

    .plyr__control.plyr__control--overlaid {
        background: transparent;
        width: 48px;
        height: 48px;
        background: url('../img/play.png') no-repeat center / 100%;


        svg {
            display: none;
        }
    }

    .plyr.plyr--stopped .plyr__controls {
        display: none
    }

    .videowrap {
        position: relative;
        overflow: hidden;

        .elvid {
            // content: "";
            display: inline-block;
            // width: 57px;
            // height: 1px;
            // background: #84F7A0;
            // z-index: 5;
            // position: absolute;
            // right: -16px;
            // top: 0px;
            // transform-origin: 0 0;
            // transform: rotate(45deg);

            width: 57px;
            height: 57px;
            border-left: 1px solid rgba(132, 247, 160, 0.3);
            background: #111011;
            position: absolute;
            right: -29px;
            top: -28px;
            z-index: 5;
            overflow: hidden;
            transform: rotate(-45deg);

            img {
                mix-blend-mode: soft-light;
                position: absolute;
                left: 0;
                top: 0;
            }
        }
    }

    .plyr {
        // clip-path: polygon(5% 0, 100% 0%, 100% 100%, 100% 100%, 0 100%, 0% 10%);
        // clip-path: polygon(5% 0, 101% 0%, 101% 101%, 101% 101%, 0 101%, -1% 10%);
        // clip-path: polygon(-1% 0, 96% 0, 101% 9%, 101% 101%, 0 101%, 0% 101%);
        border: 1px solid rgba(132, 247, 160, 0.3);
    }




}

.about__bgel1 {
    position: absolute;
    right: 70px;
    top: 35px;
}

.about__bgel2 {
    position: absolute;
    left: 50px;
    top: 170px;
}

.means {
    background: #000201;
    padding: 100px 0;

    h3, h2 {
        margin-bottom: 24px;
        line-height: 1;
    }

    p {
        max-width: 486px;
        color: var(--light-grey);
        margin-bottom: 56px;
        font-size: 14px;
    }
}

.meanbox {
    height: 247px;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    overflow: hidden;
    transition: 0.3s;

    &::after {
        content: '';
        display: block;
        width: 100%;
        height: 100%;
        background: rgba(#D9D9D9, 0.05);
        position: absolute;
        left: 0;
        top: 0;
        z-index: 1;
        opacity: 0;
        transition: 0.3s;
    }

    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        object-position: 50% 50%;
        position: absolute;
        z-index: 1;
        transition: 0.3s;
    }

    span {
        position: relative;
        display: inline-block;
        z-index: 2;
        font-family: "Unbounded", sans-serif;
        font-size: 20px;
        font-weight: 500;
        color: #fff;
        padding: 0 14px;
        text-transform: uppercase;

        &::after,
        &::before {
            content: '';
            position: absolute;
            top: -3px;
            width: 12px;
            height: 26px;
        }

        &::after {
            right: 0;
            background: url('../img/textel-right.svg') no-repeat center / 100%;
        }

        &::before {
            left: 0;
            background: url('../img/textel-left.svg') no-repeat center / 100%;
        }
    }

    &:hover {
        img {
            filter: blur(4px);
        }

        &::after {
            opacity: 1;
        }
    }
}

.commander {
    padding: 100px 0;
    background: #000201;
}

.commander__info {
    position: relative;

    &::after {
        content: '';
        position: absolute;
        display: inline-block;
        width: 6px;
        height: 6px;
        border-top: 1px solid rgb(132, 247, 160);
        border-left: 1px solid rgb(132, 247, 160);
        left: 0;
        top: 0;
        transition: 0.3s;
        animation-timing-function: ease-in-out;
    }

    &::before {
        content: '';
        position: absolute;
        display: inline-block;
        width: 6px;
        height: 6px;
        border-bottom: 1px solid rgb(132, 247, 160);
        border-left: 1px solid rgb(132, 247, 160);
        bottom: 0;
        left: 0;
        transition: 0.3s;
        animation-timing-function: ease-in-out;
    }

    .commander__infoels {
        position: relative;
        z-index: 2;
        padding: 60px 40px;
        border: 1px solid rgba(#84F7A0, 0.3);

        &::after {
            content: '';
            position: absolute;
            display: inline-block;
            width: 6px;
            height: 6px;
            border-top: 1px solid rgb(132, 247, 160);
            border-right: 1px solid rgb(132, 247, 160);
            right: -1px;
            top: -1px;
            transition: 0.3s;
        }

        &::before {
            content: '';
            position: absolute;
            display: inline-block;
            width: 6px;
            height: 6px;
            border-bottom: 1px solid rgb(132, 247, 160);
            border-right: 1px solid rgb(132, 247, 160);
            bottom: 0;
            right: 0;
            transition: 0.3s;
        }
    }

    .subtitle {
        color: #42795C;
        font-size: 14px;
        line-height: 140%;
        margin-bottom: 8px;
        font-family: "Unbounded", sans-serif;
        max-width: 100%;
    }

    h3, .titlename {
        color: #fff;
        margin-bottom: 24px;
    }

    .titlename {
        font-size: 40px;
        font-family: "Unbounded", sans-serif;
        font-weight: 500;
        line-height: 120%;
        text-transform: uppercase;
    }

    p {
        max-width: 565px;
        color: var(--light-grey);
        font-size: 14px;
        margin-bottom: 29px;
    }

    .greenbtn {
        max-width: 174px;
        width: 100%;
        text-align: center;
        font-size: 16px;
        height: 54px;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .commanderimg {
        position: absolute;
        right: -75px;
        top: 0;
        height: 100%;
        z-index: 1;
    }

}

.ideologic {
    padding: 100px 0 70px;
    background: #000201;

    h3 {
        margin-bottom: 24px;
        line-height: 120%;
    }

    p {
        max-width: 486px;
        color: var(--light-grey);
        font-size: 14px;
        margin-bottom: 56px;

        span {
            color: #42795C;
        }
    }

    .subtitle {
        font-size: 14px;
        font-family: "Unbounded", sans-serif;
        font-weight: 300;
        color: #fff;
        margin-bottom: 24px;
    }
}

.ideologic__boxwrap {
    position: relative;
    overflow: hidden;
}

.ideologic__box {
    height: 195px;
    border: 1px solid rgba(#FFF6F1, 0.2);
    background: rgba(#C4C4C4, 0.05);
    text-align: center;
    margin-bottom: 30px;
    position: relative;
    cursor: pointer;

    .imgwrap {
        margin-bottom: 5px;

        img {
            height: 80px;
            margin-bottom: 12px;
        }
    }

    p.title {
        margin-bottom: 0;
        font-size: 20px;
        line-height: 130%;
        color: #fff;
        font-family: "Unbounded", sans-serif;
        font-weight: 600;
        text-transform: uppercase;
    }

    p {
        color: var(--dark-grey);
        margin: 0;
        font-size: 14px;
    }

    &::after {
        content: '';
        display: inline-block;
        width: 12px;
        height: 12px;
        background: #000201;
        position: absolute;
        right: -6px;
        top: -6px;
        border-left: 1px solid rgba(#FFF6F1, 0.2);
        transform: rotate(-45deg);
    }

    .box-wrapper {
        overflow: hidden;
        position: relative;
        width: 100%;
        height: 100%;
        padding: 22px 16px 16px 16px;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .bottbox {
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        overflow: auto;
        background: #0a0c0b;
        padding: 16px;
        top: 100%;
        transition: 0.5s;

        p {
            font-size: 11.5px;
            color: var(--dark-grey);
            margin: 0;
            line-height: 140%;
        }

        p.title {
            margin-bottom: 8px;
            font-family: "Unbounded", sans-serif;
            color: #fff;
            font-size: 16px;
            font-weight: 500;
            line-height: 130%;
            text-transform: none;
        }

        &::-webkit-scrollbar {
            width: 6px;
        }

        &::-webkit-scrollbar-track {}

        &::-webkit-scrollbar-thumb {
            background-color: #42795C;
            border-radius: 10px;
        }
    }

    &:hover {
        .bottbox {
            top: 0;
        }
    }
}


.formsection {
    padding: 100px 0;
    position: relative;
    // overflow: hidden;

    img.aboutbg {
        mix-blend-mode: soft-light;
        position: absolute;
        left: 0;
        top: 0;
    }

    .formblock {
        padding: 50px 40px;
        background: rgba(#84F7A0, 0.03);
        border: 1px solid rgba(#84F7A0, 0.3);

        .elvid {
            display: inline-block;
            width: 57px;
            height: 57px;
            border-left: 1px solid rgba(132, 247, 160, 0.3);
            background: #111011;
            position: absolute;
            right: -16px;
            top: -28px;
            z-index: 5;
            overflow: hidden;
            transform: rotate(-45deg);

            img {
                mix-blend-mode: soft-light;
                position: absolute;
                left: 0;
                top: 0;
            }
        }
    }

    h3 {
        margin-bottom: 5px;
    }

    p {
        color: var(--light-grey);
        font-size: 14px;
        padding-right: 30px;
    }

    .container {
        position: relative;
        z-index: 2;
    }

    form {
        // padding-left: 40px;
    }

    .inputfield {
        margin-bottom: 30px;

        label {
            margin-bottom: 5px;
            font-family: "Unbounded", sans-serif;
            font-size: 14px;
            color: var(--dark-grey);
        }

        input {
            border: none;
            border-bottom: 1px solid rgba(#F1F1F1, 0.4);
            height: 36px;
            background: transparent;
            width: 100%;
            outline: none;
            color: rgba(245, 245, 245);
            font-family: "Unbounded", sans-serif;
            font-size: 14px;
            transition: 0.3s;

            &:focus {
                border-color: #84F7A0;
            }
        }

        textarea {
            border: none;
            border-bottom: 1px solid rgba(#F1F1F1, 0.4);
            height: 36px;
            background: transparent;
            width: 100%;
            outline: none;
            color: rgba(245, 245, 245);
            font-family: "Unbounded", sans-serif;
            font-size: 14px;
            transition: 0.3s;
            resize: none;

            &:focus {
                border-color: #84F7A0;
            }

            &::-webkit-scrollbar {
                width: 6px;
            }

            &::-webkit-scrollbar-track {}

            &::-webkit-scrollbar-thumb {
                background-color: #42795C;
                border-radius: 10px;
            }
        }
    }

    input[type="date"]::-webkit-calendar-picker-indicator {
        color: rgba(0, 0, 0, 0);
        opacity: 1;
        display: block;
        background: url('../img/calendar.png') no-repeat;
        width: 20px;
        height: 20px;
        border-width: thin;
        margin-top: 5px;
    }

    .radiowrap {
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        padding-top: 10px;

    }

    .radiobox {
        width: 100%;
        max-width: 90px;
        flex: 0 0 90px;
        padding-right: 10px;

        input {
            display: none;

            &:checked+label {
                &::after {
                    background: url('../img/check-active.png') no-repeat center / 100%;
                }
            }
        }

        label {
            position: relative;
            padding-left: 28px;
            margin: 0;
            display: block;
            color: #fff;
            font-size: 14px;
            font-family: "Unbounded", sans-serif;
            cursor: pointer;

            &::after {
                content: '';
                display: inline-block;
                border-radius: 50%;
                width: 20px;
                height: 20px;
                background: url('../img/check.png') no-repeat center / 100%;
                position: absolute;
                left: 0;
                top: 0;
            }
        }
    }

    .greenbtn {
        border: none;
        width: 100%;
        max-width: 174px;
        font-size: 16px;
        height: 54px;
        display: flex;
        align-items: center;
        justify-content: center;
    }


}

.commanderimgmob {
    display: none;
}

html {
    scroll-behavior: inherit !important;
}

.titleimg {
    display: inline-block;
    margin-bottom: 40px;
    max-width: 100%;
}

.herosection__info {
    .subtitle {
        font-family: "Unbounded", sans-serif;
        font-size: 20px;
        font-weight: 300;
        max-width: 100%;
        margin-bottom: 40px;

        span {
            color: #42795C;
        }
    }

    .greenbtn {
        min-height: 52px;
        display: inline-flex;
        align-items: center;
        justify-content: center;
    }
}

.about h2 {
    position: relative;
    z-index: 2;
}

.meanbox {
    cursor: pointer;
}

.imitselectwrapper {
    position: relative;
}

.imitselect {
    height: 40px;
    border-radius: 8px;
    padding: 4px 40px 4px 14px;
    color: var(--dark-grey);
    font-size: 14px;
    font-weight: 300;
    font-family: "Unbounded", sans-serif;
    border: 1px solid var(--dark-grey);
    position: relative;
    display: flex;
    align-items: center;
    cursor: pointer;
    margin-top: 11px;

    p {
        margin: 0;
    }

    .choosetext {}

    .count {
        display: none;
    }

    &::after {
        content: '';
        display: inline-block;
        width: 20px;
        height: 20px;
        position: absolute;
        right: 14px;
        top: 10px;
        background: url('../img/selectarrow.svg') no-repeat center / 100%;
    }
}

.inputfield .imitselect__dd {
    border: 1px solid var(--dark-grey);
    padding: 14px;
    border-radius: 8px;
    position: absolute;
    top: 50px;
    background: #171918;
    z-index: 2;
    width: 100%;
    display: none;

    input {
        display: none;

        &:checked+label {
            &::after {
                border-color: var(--green);
                background: url('../img/check-ic.svg') no-repeat center / 10px;
            }
        }
    }

    label {
        margin: 0;
        display: block;
        font-size: 12px;
        font-weight: 300;
        color: var(--light-grey);
        font-family: "Unbounded", sans-serif;
        padding-left: 28px;
        position: relative;

        &::after {
            content: '';
            display: inline-block;
            width: 20px;
            height: 20px;
            border: 1px solid var(--dark-grey);
            border-radius: 5px;
            position: absolute;
            left: 0;
            top: 50%;
            transform: translateY(-50%);
        }
    }

    li {
        margin-bottom: 20px;

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.hidden {
    display: none;
}

.otherinp {
    display: none;

    textarea {
        height: 50px !important;
    }
}

.formsection p i {
    font-style: normal;
}

.likeh2 {
    margin-bottom: 62px;

    h1 {
        display: inline;
    }
}

.commander,
.ideologic {
    h2.subtitle {
        text-transform: none;
        font-weight: 400;
    }

    .likeh2 {
        font-size: 40px;
        max-width: 100%;
        color: #42795C;
    }
}





@media(max-width:1399.8px) {
    .commander__info .title {
        font-size: 52px;
    }

    .ideologic__box {
        height: 210px;
    }
}

