+                    uncheckedItems.forEach(item => ul.append(item));
+                }
+
+                if (otherCheckbox.checked) {
+                    otherInputBlock.style.display = 'block';
+                } else {
+                    otherInputBlock.style.display = 'none';
+                }
+            });
+        });
+
+    }
+
+    // ----------------
+
+
+
+
