+        $(this).next('.faqbody').slideToggle();
+    });
+
+    if ($('.faqpage').length > 0) {
+        var headers = document.querySelectorAll('.faqpage h3');
+        for (var i = 0; i < headers.length; i++) {
+            headers[i].setAttribute('id', i + 1);
+        }
+
+
+        var headers = document.querySelectorAll('.faqpage h3');
